INSERT INTO `wp_options` VALUES ('182', 'marketers_delight_css', '/**\n * Marketers Delight\n * by Alex Mangini, kolakube.com\n * Get help: kolakube.com/forum/\n */\n\n/**\n * Table of contents:\n * — packages\n * — shortcodes + widgets\n * - page leads\n * — basic landing page template\n * — advanced landing page template\n * — responsive styles\n*/\n\n\n/*---:[ packages ]:---*/\n\n&body\n&links\n&forms\n&buttons\n&header\n&logo\n&headermenu\n&mainmenu\n&2columns\n&entry\n&author\n&comments\n&sidebarwidgets\n&footer\n\n/*---:[ shortcodes + widgets ]:---*/\n\n/* styles widget boxes */\n.box-style { background-color: $w; border: 1px solid $g; padding: $single; $round_corners; $small_bot_shadow; }\n\n/* post optin form (is shared) */\n.post-optin { padding: $single; text-align: center; }\n.post-optin input { margin: 0 $half $half 0; }\n\n/* content block (shortcode only, is shared) */\n.content-block { border: 1px solid $g; border-width: 1px 0; margin-bottom: $single; padding-bottom: $single; }\n\n/* alert + note (is shared) */\n.alert { background-color: $d; color: $w; }\n\n/* popular articles (widget-only) */\n.sidebar .popular-articles ul { list-style: none; margin-left: 0; }\n.popular-articles li { border-bottom: 1px solid $g; margin-bottom: $half; padding-bottom: $half; }\n.popular-articles li:last-child { border-bottom: 0; margin-bottom: 0; padding-bottom: 0; }\n.popular-articles .icon { color: $g; font-size: 11px; margin-right: $fourth; }\n.popular-articles .comment-text { color: $d; }\n\n/* affiliate box (widget-only) */\n.affiliate-box { text-align: center; }\n.affiliate-box img { margin-bottom: $half; }\n\n/* quotes box (shortcode + widget) */\n.quote-box { margin-bottom: $half; position: relative; }\n.quote-box:before, .quote-box:after { border-style: solid; }\n.quote-box:before { border-color: $w transparent transparent $w; border-width: 12px; bottom: -24px; right: 40px; z-index: 20; }\n.quote-box:after { border-color: rgba(0, 0, 0, .08) transparent transparent rgba(0, 0, 0, .08); border-width: 14px; bottom: -29px; right: 38px; z-index: 10; }\n.quote-box img.right, .quote-box img.featured { float: right; margin: 0 0 $single $single; }\n.quote-box img.left { margin: 0 $single $single 0; }\n.quote-box .quote-image { margin-bottom: $half; $circle; }\n.quote-source { color: $dg; font-style: italic; margin-right: 90px; text-align: right; }\n\n/* shared */\n.post-optin h3, .post-optin h4, .alert h3, .alert h4, .note h3, .note h4, .content-block h3, .content-block h4 { margin-top: 0; }\n.note, .post-optin { background-color: $lg; border: 1px solid $g; border-width: 1px 0; }\n.alert, .note, .post-optin, .content-block { margin: 0 -$double $single; padding: $single $double; }\n\n\n/*---:[ page leads ]:---*/\n\n.page-lead + .page-lead { margin-top: $double; }\n.page-lead .page { padding-top: $double; }\n.page-lead .text-area + .three-columns, .page-lead .three-columns + .text-area, .video-lead .video-embed { margin-top: $mid; }\n.funnel-lead .page, .pricing-lead .page { padding-left: 0; padding-right: 0; }\n.page-lead ul, .page-lead ol { list-style: square; }\n\n.text-area h1 + p, .text-area h2 + p, .text-area h3 + p { font-size: 18px; line-height: 29px; }\n.text-area h1, .text-area h2, .text-area h3, .text-area h4 { margin-bottom: $fourth; }\n\n\n/*---:[ basic landing page template ]:---*/\n\n.basic .page { margin: 0 auto; width: $content_width; }\n.basic .header-area, .basic .footer, .basic .headline { text-align: center; }\n.basic .logo, .basic .content { float: none; margin: 0 auto; }\n.basic .footer { padding: $single 0; }\n\n\n/*---:[ advanced landing page template ]:---*/\n\n.basic .headline-area, .advanced .headline-area { text-align: center; }\n\n/* layout + spacing */\n.advanced .header-area { margin-bottom: $mid; }\n.advanced .header-area + .landing-full { margin-top: -$mid; }\n.landing-box .page { padding: 0; }\n.advanced .page, .advanced .content { width: 888px; }\n.advanced .content-box { margin-bottom: $mid; padding-top: 0; }\n.advanced .content-box .content .entry { padding: 60px 144px; }\n.advanced .header-area, .advanced .footer, .advanced .text-area { text-align: center; }\n.advanced .logo, .advanced .content { float: none; }\n.advanced .footer { margin-top: $half; padding: $single 0; }\n\n/* text area */\n.landing-box .text-area { margin-bottom: $mid; }\n.landing-box .text-area h1, .landing-box .text-area h2, .landing-box .text-area h3 { font-size: 36px; line-height: 51px; }\n.landing-box .text-area h3 { margin-top: 0; }\n\n/* full width */\n.landing-full { background-color: $p; margin-bottom: $mid; }\n.landing-full .page { padding: $double 0; }\n.landing-full .text-area { color: $w; }\n.landing-full .entry .text-area, .landing-full .col .text-area { color: inherit; }\n.landing-full .text-area:last-child { margin-bottom: 0; }\n\n/* content box */\n.landing-box .content { $large_shadow; }\n.landing-box .content.entry { margin-bottom: $single; padding: 60px 144px; }\n.landing-box .entry { padding: 0 $half; }\n.landing-box .content.round-top { border-width: 1px 1px 0; margin-bottom: 0; -moz-border-radius: 6px 6px 0 0; -webkit-border-radius: 6px 6px 0 0; border-radius: 6px 6px 0 0; }\n.landing-box .content.round-bottom { border-width: 0 1px 1px; -moz-border-radius: 0 0 6px 6px; -webkit-border-radius: 0 0 6px 6px; border-radius: 0 0 6px 6px; }\n.landing-box .content.round-full { $round_corners_6; }\n.landing-box .content-text { margin-bottom: $single; }\n\n/* columns */\n.landing-box .col { float: left; margin-left: 3%; padding: 0 $single; text-align: center; }\n.landing-box .col .text-area p { font-size: 15px; line-height: 22px; }\n.landing-box .col .text-area p, .landing-box .col .text-area { margin-bottom: 22px; }\n.landing-box .col .text-area h4 { font-size: 21px; line-height: 29px; }\n.landing-box .col p { margin-bottom: $single; }\n.landing-box .col .icon { font-size: $mid; margin-bottom: $single; }\n.landing-box .box-cols .col { background-color: $w; border: 1px solid $g; $round_corners_6; $large_shadow; }\n.landing-box .two-columns.box-text, .landing-box .three-columns.box-text { margin-bottom: $double; }\n.landing-box .two-columns.box-cols, .landing-box .three-columns.box-cols { margin-bottom: $single; }\n.landing-box .box-cols + .box-text { margin-top: $double; }\n.landing-box .two-columns .col { width: 48.5%; }\n.landing-box .two-columns.box-cols .col { padding: $mid; }\n.landing-box .three-columns .col { padding: 0 $half; width: 31.333333333333%; }\n.landing-box .three-columns.box-cols .col { padding: $mid $single; }\n\n\n/*--:[ responsive break: 1 ]:--*/\n\n@media screen and (max-width: 1080px) {\nimg, video { height: auto !important; max-width: 100% !important; }\n.page, .landing-box .content { width: 100% !important; }\n.page-lead .page { padding-left: $double; padding-right: $double; }\nsection.content { border-left: 0; width: 62.222222222222%; /* 672 */ }\naside.sidebar { width: 37.777777777778%; /* 408 */ }\n\n/* videos */\n.video-box { height: 0; padding-bottom: 56.25%; padding-top: 25px; position: relative; }\n.video-box iframe { height: 100%; left: 0; position: absolute; top: 0; width: 100%; }\n\n/* product lead */\n.video-lead .email-code { width: 100%; }\n\n/* funnel lead */\n.funnel-lead .col { margin-left: 3%; width: 31.33333333333333%; }\n\n/* orb lead */\n.orb-lead .col { margin: 0 0 $single 5%; }\n\n/* pricing lead */\n.pricing-lead .pricing-badge { right: 15px; top: 15px; }\n.pricing-lead .col { overflow: hidden; width: 33.33333333333333%; }\n\n/* advanced landing page template */\n.landing-box .page { padding-left: $half; padding-right: $half; }\n.landing-full .page { padding-bottom: $mid; padding-top: $mid; }\n.advanced .content-box .page, .advanced .content-box .content { width: 100%; }\n.advanced .content-box .content { padding-top: $single; }\n.landing-box .content { padding: $double; }\n.landing-box .box-text .col { padding-left: $half; padding-right: $half; }\n.landing-box .text-area { padding-left: $half; padding-right: $half; }\n.landing-box .col .text-area { padding-left: 0; padding-right: 0; }\n.content-text .alert, .content-text .note, .content-text .post-optin, .content-text .content-block { margin: 0 -$single $single; padding: $single !important; }\n}\n\n\n/*--:[ responsive break: 2 ]:--*/\n\n@media screen and (max-width: 850px) {\n/* header */\n.header-area .logo { display: block; float: none; text-align: center; }\ndiv#res-menu { float: none; margin-bottom: $half; text-align: center; }\nul.header-menu li { display: inline-block; float: none; }\n\n/* product lead */\n.product-lead .text-area, .product-lead .email-form { text-align: center; width: 100%; }\n.product-lead .text-area ul, .product-lead .text-area ol { text-align: left; }\n.email-lead .email-form + .text-area, .email-lead .text-area + .email-form { margin-top: $mid; }\n.email-lead .text-area + .email-form, .email-lead .email-form + .text-area, .image-lead .text-area + .image-area, .image-lead .image-area + .text-area { margin-left: 0; }\n.image-lead .image-area { text-align: center; width: 100%; }\n\n/* funnel lead */\n.funnel-lead .col { margin: 0 0 $double 10%; width: 45% }\n.funnel-lead .col:last-child { margin: 0 0 0 27.5%; }\n\n/* orb lead */\n.orb-lead { overflow: hidden; }\n.orb-lead .col { height: 250px; float: none; font-size: 14px; line-height: 21px; margin: 0 auto $single; padding-top: 40px; width: 250px; }\n.orb-lead .col.first { margin-left: auto !important; }\n.orb-lead .col:last-child { margin-bottom: 0; }\n\n/* pricing lead */\n.pricing-lead .col { margin: 0 0 $double 10%; width: 45%; }\n.pricing-lead .col:last-child { margin: 0 0 0 27.5%; }\n\n/* main menu */\n.menu-main .page { padding: 0; }\n.menu-main li { text-align: center; width: 20%; }\n.main-menu > li:first-child > a { padding-left: $single; }\n\n/* content box */\nsection.content, aside.sidebar, .footer-col { float: none; width: 100%; }\nsection.content { border-right: 0; }\n.entry blockquote.right, .entry blockquote.left { float: none; margin: 0 0 $single; width: auto; }\n.sidebar .widget input[type=\"text\"], .sidebar .widget input[type=\"email\"], .sidebar .widget input[type=\"password\"] { width: 50%; }\n\n/* footer */\n.footer-col { border-bottom: 1px solid #444; margin: 0 0 24px 0; padding-bottom: 24px; }\n.footer-col:last-child { border-bottom: 0; margin-bottom: 0; padding-bottom: 0; }\n.footer-copyright { text-align: center; }\n.footer .kol-attribution { float: none; }\n.footer .kol-attribution span { display: block; }\n\n/* advanced landing page template */\n.landing-box .box-cols .col { padding: $single !important; }\n}\n\n\n/*--:[ responsive break: 3 ]:--*/\n\n@media screen and (max-width: 600px) {\n/* general */\ndiv.page { padding-left: $single; padding-right: $single; }\n\n/* logo */\n.logo { line-height: 54px; }\n\n/* page leads */\n.page-lead .page { padding-left: $single; padding-right: $single; padding-top: $mid; }\n.page-lead + .page-lead { margin-top: $mid; }\n.page-lead .text-area h1 + p, .page-lead .text-area h2 + p, .page-lead .text-area h3 + p { font-size: 16px; line-height: 24px; }\n\n/* product lead */\n.video-lead input { margin-bottom: $half; width: 100% !important; }\n\n/* funnel lead */\n.funnel-lead .col { margin: 0 0 $single; padding: $single $half; width: 100%; }\n.funnel-lead .col:last-child { margin-left: 0; }\n.funnel-lead .col .icon { font-size: 20px; height: 60px; padding-top: 20px; width: 60px; margin-top: 0; }\n.funnel-lead .col .button { display: block; }\n\n/* pricing lead */\n.pricing-lead .col { float: none; margin: 0 0 $single; width: 100% }\n.pricing-lead .col:last-child { margin-left: 0; }\n\n/* reset header menu */\n.header-menu { text-align: left; }\nul.header-menu ul.sub-menu { background: none; border-color: rgba(255, 255, 255, .2); border-width: 0 0 0 1px; margin-left: $single; padding: 0; position: static; visibility: visible; $remove_shadow; }\nul.header-menu li ul.sub-menu a { border: 0; padding-left: $single; padding-top: 0; }\nul.header-menu .sub-menu, ul.header-menu ul li { width: auto; }\nul.header-menu li.menu-item a { border-top: 1px solid rgba(255, 255, 255, .2); color: $w; padding: 18px $single; }\n.header-menu > li:first-child { margin-top: $single; }\nul.header-menu li a:hover { color: rgba(255, 255, 255, .5); }\n\n/* restyle header menu */\n.res { color: $w; position: absolute; right: $single; top: $single; z-index: 9999; }\na.res-off, #res-menu:target .res-on, #res-menu:target ul.header-menu li { display: block; }\n.header-menu .sub-menu:before, .header-menu .sub-menu:after, ul.header-menu li, #res-menu:target .res-off { display: none; }\n#res-menu { background-color: $d; padding: $single 0; $round_corners; }\n.header-menu:before { content: \"Navigate\"; color: $w; display: block; font-weight: bold; margin-left: $single; }\n#res-menu:target { padding-bottom: 0 !important; }\nul.header-menu li.button { background: none !important; border: 0 !important; margin-left: inherit; padding: inherit; text-shadow: none; $remove_shadow; }\n\n/* main menu */\n.menu-main { border-bottom: 0; }\n.menu-main .sub-menu { margin: 3px 0 0 3px; width: 90%; }\n.main-menu ul li { text-align: left; width: 100%; }\n.menu-main li { width: 50%; }\n.menu-main li.button { margin: 0; }\n.menu-main li > a { border-bottom: 1px solid rgba(0, 0, 0, .15); }\n\n/* advanced landing page template (is shared) */\n.advanced .header-area + .landing-full { margin-top: -$single; }\n.advanced .content.entry { margin-bottom: $half; padding-top: $mid !important; }\n.advanced .entry { padding-top: 0 !important; }\n.advanced .header-area, .landing-box .text-area { margin-bottom: $single; }\n.landing-box .text-area p { font-size: 15px; line-height: 22px; }\n.landing-box .col { margin: 0 0 $half; }\n.landing-box .col:last-child { margin-bottom: 0; }\n.landing-box .box-text .col { margin-bottom: $mid; }\n.landing-box .two-columns.box-text, .landing-box .three-columns.box-text { margin-bottom: 0!important; }\n.landing-box 